import React, { useState } from "react";
import cn from "classnames";
import styles from "./Hero.module.sass";
import Dropdown from "../../../components/Dropdown";
import Icon from "../../../components/Icon";
import Item from "./Item";

const items = [
  {
    title: "General",
    icon: "home",
    items: [
      "What is NSFT?",
      "What is an NFT?",
      "What is minting?",
      "How do I mint a piece of digital content?",
      "How do I verify my account?",
      "How do I fund my account?",
      "I want to withdraw funds from my account, how do I do this?"
    ],
  },
  {
    title: "Creators",
    icon: "circle-and-square",
    items: [
      "What is a blockchain?",
      "What type of content should I upload and sell on NSFT?",
      "How is my ownership of an NFT protected on the blockchain?",
      "Is it anonymous?",
      "Will I receive a percentage if one of my pieces is resold in the future?",
      "As the content creator can I sell a piece more than once?",
      "Can a piece be resold after purchasing?",
    ],
  },
  {
    title: "Buyers",
    icon: "circle-and-square",
    items: [
      "How does the auction timer work?",
      "How do I bid on a piece?",
      "What can I do with my piece after I’ve received it in my account?",
      "Can I transfer my NFT to another blockchain?",
      "What blockchain does NSFT run on?",
    ],
  },
  {
    title: "Technical",
    icon: "lightning",
    items: [
      "What blockchain does NSFT run on?",
      "How does it work",
      "How to start with Stacks",
      "Dose it suppport Dark Mode",
      "What is Stacks Design System",
    ],
  },
  {
    title: "Privacy",
    icon: "pen",
    items: [
      "Is my personal information protected?",
      "How do I verify my account?",
      "Dose it suppport Dark Mode",
      "Does it support Auto-Layout",
      "What is Stacks Design System",
    ],
  },
];

const Hero = () => {
  const options = [];
  items.map((x) => options.push(x.title));

  const [direction, setDirection] = useState(options[0]);

  return (
    <div className={cn("section", styles.section)}>
      <div className={cn("container", styles.container)}>
        <div className={styles.top}>
          <div className={styles.stage}>learn how to get started</div>
          <h1 className={cn("h2", styles.title)}>Frequently asked questions</h1>
          <div className={styles.info}>
            Common community questions are below, you can also {" "}
            <a href="mailto:aidan@nsftonight.com" rel="noopener noreferrer">
              Contact Support
            </a>
          </div>
          <Dropdown
            className={cn("mobile-show", styles.dropdown)}
            value={direction}
            setValue={setDirection}
            options={options}
          />
        </div>
        <div className={styles.row}>
          <div className={styles.col}>
            <div className={styles.nav}>
              {items.map((x, index) => (
                <div
                  className={cn(styles.link, {
                    [styles.active]: x.title === direction,
                  })}
                  onClick={() => setDirection(x.title)}
                  key={index}
                >
                  <Icon name={x.icon} size="16" />
                  <span>{x.title}</span>
                </div>
              ))}
            </div>
          </div>
          <div className={styles.col}>
            {items
              .find((x) => x.title === direction)
              .items.map((x, index) => (
                <Item className={styles.item} item={x} key={index} />
              ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
