import { useEffect, useReducer } from "react";
import { defaultReducer } from "../reducer/defaultReducer";
import Identicon from "identicon.js";
import axios from "axios";
import { AVATAR_DEFAULT } from "../components/Header/User/avatar-default";

let api_node;
process.env.NODE_ENV === "production"
  ? api_node = ''
  : api_node = process.env.REACT_APP_LOCAL_API_NODE

export default function useProfileData(user) {
    const [state, dispatch] = useReducer(defaultReducer, {
        loading: false,
        error: false,
        data: {
            avatar: '',
            name: 'new_user',
            handle: user?.addr,
            cover_image: false,
            profile_image: false,
            email: '',
            verified: false,
            bio: '',
            url: null,
            twitter: null,
            instagram: null
          }
      })



      useEffect(() => {
        const fetchUserData = async () => {
          dispatch({ type: 'PROCESSING' })
          let hash;
          !user ? hash = '0x0000000000000' : hash = user?.addr
          let avatar = new Identicon(hash).toString()
          try {
            const api = await axios.get(`${api_node}/api/v1/user/${user?.addr}`)
            const serverResponse = api.data;
            console.log(serverResponse)
            if (serverResponse.length > 0) {
              dispatch({ type: 'SUCCESS', payload: serverResponse[0] })
            } else {
              dispatch({ type: 'SUCCESS', payload: {
                avatar: avatar,
                name: 'new_user',
                handle: user?.addr,
                cover_image: false,
                profile_image: false,
                email: '',
                bio: '',
                verified: false,
                url: null,
                twitter: null,
                instagram: null
              }})
            }
          } catch(err) {
            dispatch({ type: 'ERROR' })
          }
        }
        console.log(state.data.verified)
        if (!state.data.verified) {
          fetchUserData()
        }
      }, [user])

    return {
        ...state,
    }
}
