import React, { useEffect, useState } from "react";
import cn from "classnames";
import styles from "./User.module.sass";
import Icon from "../../../components/Icon";
import { useUser } from "../../../providers/UserProvider";
import { useAuth } from "../../../providers/AuthProvider";
import axios from "axios";
import { Redirect } from "react-router";
// import { isStepDivisible } from "react-range/lib/utils";

let api_node;
process.env.NODE_ENV === "production"
  ? api_node = ''
  : api_node = process.env.REACT_APP_LOCAL_API_NODE


const User = ({ className, handle }) => {
  const [visible, setVisible] = useState(false);
  const [verifying, setVerifying] = useState(false)
  const [profData, setProfData] = useState({})
  console.log(profData)
  const socials = [
    {
      title: "twitter",
      url: `https://twitter.com/${profData.twitter}`,
    },
    {
      title: "instagram",
      url: `https://www.instagram.com/${profData.instagram}`,
    },
  ];

  const { user } = useAuth()
  const { collection, profile, createCollection, fetchUserData } = useUser()

  useEffect(() => {
    setProfData(profile)
    if (profile.handle === handle) return
    const getProfileData = async () => {
      try {
        const api = await axios.get(`${api_node}/api/v1/handle/${handle}`)
        const addr = api.data[0]
        console.log(addr)
        const data = await axios.get(`${api_node}/api/v1/user/${addr.address}`)
        setProfData(data.data[0])
      } catch(err) {
        console.log(err)
        return <Redirect to="*" />
      }
    }
    getProfileData()
    //eslint-disable-next-line
  }, [profile.handle])

  const verify = async () => {
    setVerifying(true)
    if (!collection) {
      await createCollection()
    }
    if (!profile.db) {
      await axios.post(`${api_node}/api/v1/user`, {
        name: profile.name,
        email: profile.email,
        avatar: profile.avatar,
        db: profile.true,
        cover_image: profile.cover_image,
        profile_image: profile.profile_image,
        handle: profile.username,
        address: user?.addr,
        bio: profile.bio,
        url: profile.url,
        twitter: profile.twitter,
        instagram: profile.insta
      })
    } 
    await axios.put(`${api_node}/api/v1/user/verify`, {
      verify: true,
      address: user?.addr
    })
    await fetchUserData()
    setVerifying(false)
  }

  return (
    <>
      <div className={cn(styles.user, className)}>
        <div className={styles.avatar}>
        {profData.profile_image ? <img src={`/user-images/${profData.address}-profile.jpg`} alt="Avatar" /> : <img src={`data:image/png;base64,${profData.avatar}`} alt="Avatar" />}
        </div>
        <div className={styles.name}>{profData.name}</div>
        <div className={styles.code}>
          <div className={styles.number}>@{profData.handle}</div>
        </div>
        <div className={styles.info}>
          {profData.bio}
        </div>
        {profData.url ? <a
          className={styles.site}
          href={`https://${profData.url}`}
          target="_blank"
          rel="noopener noreferrer"
        >
          <Icon name="globe" size="16" />
          <span>{profData.url}</span>
        </a> : <></>}
        {profData.handle === handle ? <></> : (<div className={styles.control}>
          <div className={styles.btns}>
            <button
              className={cn(
                "button button-small",
                { [styles.active]: visible },
                styles.button
              )}
              onClick={() => setVisible(!visible)}
            >
              <span>Follow</span>
              <span>Unfollow</span>
            </button>
          </div>
        </div>)}
        {profile.verified ? <></> : (<div className={styles.control}>
          <div className={styles.btns}>
            <button
              className={cn(
                "button button-small",
                { [styles.active]: visible },
                styles.button
              )}
              onClick={verify}
            >
              <span>{verifying ? 'One moment...' : "Verify"}</span>
            </button>
          </div>
        </div>)}
        <div className={styles.socials}>
          {socials.map((x, index) => (
            <a
              className={styles.social}
              href={x.url}
              target="_blank"
              rel="noopener noreferrer"
              key={index}
            >
              <Icon name={x.title} size="20" />
            </a>
          ))}
        </div>
      </div>
    </>
  );
};

export default User;
