import React, { useState } from "react";
import cn from "classnames";
import styles from "./Bid.module.sass";
import Form from "../Form";
import { formatAmountInput } from "../../mocks/functions";

const items = [
  {
    title: "Enter bid",
    value: "ETH",
  },
  {
    title: "Your balance",
    value: "8.498 ETH",
  },
  {
    title: "Service fee",
    value: "0 ETH",
  },
  {
    title: "Total bid amount",
    value: "0 ETH",
  },
];

const Bid = ({ className, data }) => {
  const [bid, setBid] = useState(false)
  console.log(data)
  console.log(data.minNextBid)
  console.log(typeof(parseInt(data.minNextBid)))

  const handleSubmit = async (e) => {
    e.preventDefault();
  };

  return (
    <div className={cn(className, styles.checkout)}>
      <div className={cn("h4", styles.title)}>Place a bid</div>
      <div className={styles.info}>
        You are about to bid on <strong>{data.title}</strong>
      </div>

      <div className={styles.stage}>Your bid</div>
      <div className={styles.text}>
            Minimum bid ${formatAmountInput(parseInt(data.minNextBid))}
          </div>
      <div className={styles.table}>
        <div className={styles.row}>
          <Form
            className={styles.form}
            onSubmit={() => handleSubmit()}
            value={bid}
            placeholder="Bid Amount"
            setValue={setBid}
            type="number"
            name="bid"
            step={1}
            min={parseInt(data.minNextBid)}
          />
          {/*<div className={styles.col}>Starting Price</div>
            <div className={styles.col}>value</div>*/}
        </div>
      </div>
      {/*<div className={styles.table}>
        {items.map((x, index) => (
          <div className={styles.row} key={index}>
            <div className={styles.col}>{x.title}</div>
            <div className={styles.col}>{x.value}</div>
          </div>
        ))}
        </div>*/}
      <div className={styles.btns}>
        <button className={cn("button", styles.button)}>Place a bid</button>
        <button className={cn("button-stroke", styles.button)}>Cancel</button>
      </div>
    </div>
  );
};

export default Bid;
