import React, { useEffect, useReducer, useState } from "react";
import { Link } from "react-router-dom";
import cn from "classnames";
import OutsideClickHandler from "react-outside-click-handler";
import styles from "./User.module.sass";
import Icon from "../../Icon";
import Theme from "../../Theme";
import { useAuth } from "../../../providers/AuthProvider";
import { defaultReducer } from '../../../reducer/defaultReducer'
import axios from "axios";
import { useUser } from "../../../providers/UserProvider";
import { AVATAR_DEFAULT } from "./avatar-default";

const items = [
  {
    title: "My profile",
    icon: "user",
    url: "/profile",
  },
  {
    title: "My items",
    icon: "image",
    url: "/item",
  },
  {
    title: "Dark theme",
    icon: "bulb",
  },
  {
    title: "Disconnect",
    icon: "exit",
    url: "logOut",
  },
];

let api_node;
process.env.NODE_ENV === "production"
  ? api_node = ''
  : api_node = process.env.REACT_APP_LOCAL_API_NODE

const User = ({ className }) => {
  const [visible, setVisible] = useState(false);
  const { user, logOut } = useAuth()
  const { balance, createVault } = useUser()

  const [state, dispatch] = useReducer(defaultReducer, {
    loading: false,
    error: false,
    data: [{
      avatar: AVATAR_DEFAULT,
      name: 'new_user',
      handle: user?.addr,
    }]
  })


  useEffect(() => {
    const fetchUserData = async () => {
      dispatch({ type: 'PROCESSING' })
      try {
        const api = await axios.get(`${api_node}/api/v1/user/${user?.addr}`)
        const serverResponse = api.data;
        if (serverResponse.length > 0) {
          dispatch({ type: 'SUCCESS', payload: serverResponse })
        }
      } catch(err) {
        dispatch({ type: 'ERROR' })
      }
    }
    fetchUserData()
  }, [])

  console.log(state)

  return (
    <OutsideClickHandler onOutsideClick={() => setVisible(false)}>
      <div className={cn(styles.user, className)}>
        <div className={styles.head} onClick={() => setVisible(!visible)}>
          <div className={styles.avatar}>
            <img src={`data:image/png;base64,${state.data[0].avatar}`} alt="Avatar" />
          </div>
          <div className={styles.wallet}>
            @{state.data[0].handle}
          </div>
        </div>
        {visible && (
          <div className={styles.body}>
            <div className={styles.name}>{state.data[0].name}</div>
            <div className={styles.code}>
              <div className={styles.number}>{user?.addr}</div>
              <button className={styles.copy}>
                <Icon name="copy" size="16" />
              </button>
            </div>
            <div className={styles.wrap}>
              <div className={styles.line}>
                <div className={styles.preview}>
                </div>
                <div className={styles.details}>
                  <div className={styles.info}>Balance</div>
                  <div className={styles.price}>${balance.slice(0, -6)}</div>
                </div>
              </div>
              <button
                className={cn("button-stroke button-small", styles.button)}
              >
                Manage Funds
              </button>
            </div>
            <div className={styles.menu}>
              {items.map((x, index) =>
                x.url ? (
                  x.url.startsWith("logOut") ? (
                    <div className={styles.item} key={index}>
                      <div className={styles.icon}>
                        <Icon name={x.icon} size="20" />
                      </div>
                      <div className={styles.text} onClick={logOut}>{x.title}</div>
                      </div>

                  ) : (
                    <Link
                      className={styles.item}
                      to={x.url}
                      onClick={() => setVisible(!visible)}
                      key={index}
                    >
                      <div className={styles.icon}>
                        <Icon name={x.icon} size="20" />
                      </div>
                      <div className={styles.text}>{x.title}</div>
                    </Link>
                  )
                ) : (
                  <div className={styles.item} key={index}>
                    <div className={styles.icon}>
                      <Icon name={x.icon} size="20" />
                    </div>
                    <div className={styles.text}>{x.title}</div>
                    <Theme className={styles.theme} />
                  </div>
                )
              )}
            </div>
          </div>
        )}
      </div>
    </OutsideClickHandler>
  );
};

export default User;
