import React, { useEffect, useState } from "react";
import cn from "classnames";
import styles from "./ProfileEdit.module.sass";
import Control from "../../components/Control";
import TextInput from "../../components/TextInput";
import TextArea from "../../components/TextArea";
import { useAuth } from "../../providers/AuthProvider";
import { useUser } from "../../providers/UserProvider";
import axios from "axios";
import Form from "../../components/Form";
import { mutate, tx } from "@onflow/fcl";
import { MINT_UTILITY_COIN } from "../../flow/mint-utility-coin.tx";
import Loader from "../../components/Loader";
import { formatAmountInput } from "../../mocks/functions";
import { authorizationFunction } from "../../services/authorization-function";
import { useHistory } from "react-router";

const breadcrumbs = [
  {
    title: "Home",
    url: "/",
  },
  {
    title: "Edit Profile",
  },
];

let api_node;
process.env.NODE_ENV === "production"
  ? (api_node = "")
  : (api_node = process.env.REACT_APP_LOCAL_API_NODE);

const ProfileEdit = () => {
  const { user } = useAuth();
  const { profile, getBalance, collection, fetchUserData } = useUser();
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [change, setChange] = useState(false);
  const [bio, setBio] = useState("");
  const [url, setUrl] = useState("");
  const [twitter, setTwitter] = useState("");
  const [insta, setInsta] = useState("");
  const [email, setEmail] = useState("");
  const [errors, setErrors] = useState(false);
  const [deposit, setDeposit] = useState(0);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(false);
  const [updateSuccess, setUpdateSuccess] = useState(false)
  const [verified, setVerified] = useState(false)
  const [src, setSrc] = useState('')

  function uploadFile(file, signedRequest, url) {
    const xhr = new XMLHttpRequest();
    xhr.open('PUT', signedRequest);
    xhr.onreadystatechange = () => {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          console.log('setting source', url)
          setSrc(url)
        }
        else {
          alert('Could not upload file');
        }
      }
    };
    xhr.send(file);
  }

  const getSignedRequest = (file) => {
    const xhr = new XMLHttpRequest();
    console.log(file.name, file.type)
    xhr.open('GET', `${api_node}/api/v1/sign-s3?file-name=${file.name}&file-type=${file.type}`);
    xhr.onreadystatechange = () => {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          console.log(xhr.responseText)
          const response = JSON.parse(xhr.responseText);
          console.log('response', response)
          uploadFile(file, response.signedRequest, response.url)
        }
        else {
          alert('Could not get signed URL');
        }
      }
    };
    xhr.send();
  }

  const onProfilePhotoChange = (e) => {

    const file = e.target.files[0]
    if (file == null) {
      return alert('No file selected.');
    }
    console.log(file)
    getSignedRequest(file);



