    setUrl(e);
  };

  const handleTwitterChange = async (e) => {
    setTwitter(e);
  };

  const handleInstagramChange = async (e) => {
    setInsta(e);
  };

  const handleEmailChange = async (e) => {
    setEmail(e);
  };

  useEffect(() => {
    setName(profile.name);
    setUsername(profile.handle);
    setBio(profile.bio);
    setUrl(profile.url);
    setTwitter(profile.twitter);
    setInsta(profile.instagram);
    setEmail(profile.email);
    setSrc(`data:image/png;base64,${profile.avatar}`)
  }, [profile]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let taken = false;

    if (change) {
      const API = await axios.get(`${api_node}/api/v1/usernames/${username}`);
      const serverResponse = API.data;

      if (serverResponse[0].num_unique !== '0') {
        taken = true;
      }
    }
    if (taken) {
      setErrors(true);
      return;
    } else {
      profile.db
        ? axios.put(`${api_node}/api/v1/user/update`, {
            name: name,
            email: email,
            handle: username,
            cover_image: profile.cover_image,
            profile_image: profile.profile_image,
            address: user?.addr,
            bio: bio,
            url: url,
            twitter: twitter,
            instagram: insta,
          })
        : axios.post(`${api_node}/api/v1/user`, {
            name: name,
            email: email,
            avatar: profile.avatar,
            db: true,
            cover_image: profile.cover_image,
            profile_image: profile.profile_image,
            handle: username,
            address: user?.addr,
            bio: bio,
            url: url,
            twitter: twitter,
            instagram: insta,
            verified: profile.verified
          });
      setUpdateSuccess(true)
      //await fetchUserData()
      setTimeout(() => {
        window.location.reload();
      }, 500)
    }
  };

  const onDepositSubmit = async (e) => {
    e.preventDefault();
    if (!collection) {
      setVerified(true)
      return
    }
    setLoading(true);
    try {
      let res = await mutate({
        cadence: MINT_UTILITY_COIN,
        args: (arg, t) => [
          arg(user?.addr, t.Address),
          arg(formatAmountInput(deposit), t.UFix64),
        ],
        limit: 200,
        authz: authorizationFunction
      });
      let txStatus = await tx(res).onceSealed();
      setSuccess(true);
      setLoading(false);
      getBalance()
      return txStatus;
    } catch (err) {
      setError(true);
      setLoading(false);
      console.log(err);
    }
  };

  return (
    <div className={styles.page}>
      <Control className={styles.control} item={breadcrumbs} />
      <div className={cn("section-pt80", styles.section)}>
        <div className={cn("container", styles.container)}>
          <div className={styles.top}>
            <h1 className={cn("h2", styles.title)}>Edit profile</h1>
          </div>
          <div className={styles.row}>
            <div className={styles.col}>
              <div className={styles.user}>
                <div className={styles.avatar}>
                    <img
                      id="preview"
                      src={src}
                      alt="Avatar"
                    />
                </div>
                <div className={styles.details}>
                  <div className={styles.stage}>Profile photo</div>
                  <div className={styles.file}>
                    <button
                      className={cn(
                        "button-stroke button-small",
                        styles.button
                      )}
                    >
                      Upload
                    </button>
                    <input
                      className={styles.load}
                      type="file"
                      name="file"
                      accept="image/*"
                      onChange={onProfilePhotoChange}
                    />
                  </div>
                      </div>
              </div>
            </div>
            <div className={styles.col}>
              <div className={styles.list}>
                <div className={styles.item}>
                  <div className={styles.category}>Account info</div>
                  <div className={styles.fieldset}>
                    <TextInput
                      className={styles.field}
                      label="name"
                      name="Name"
                      type="text"
                      placeholder={
                        profile.name ? profile.name : "Enter your name"
                      }
                      required
                      onChange={(e) => handleNameChange(e.currentTarget.value)}
                    />
                    <TextInput
                      className={styles.field}
                      label="Username"
                      name="Handle"
                      type="text"
                      placeholder={
                        profile.handle ? profile.handle : "Enter your Username"
                      }
                      required
                      onChange={(e) =>
                        handleUsernameChange(e.currentTarget.value)
                      }
                    />

                    <TextInput
                      className={styles.field}
                      label="email"
                      name="Email"
                      type="text"
                      placeholder={
                        profile.email ? profile.email : "Enter your email"
                      }
                      required
                      onChange={(e) => handleEmailChange(e.currentTarget.value)}
                    />
                    <TextArea
                      className={styles.field}
                      label="Bio"
                      name="Bio"
                      placeholder={
                        profile.bio
                          ? profile.bio
                          : "About yourself in a few words"
                      }
                      required="required"
                      onChange={(e) => handleBioChange(e.currentTarget.value)}
                    />
                  </div>
                </div>
                <div className={styles.item}>
                  <div className={styles.category}>
                    Fund Your Account with USD
                  </div>
                  <div className={styles.fieldset}>
                    {loading && (
                      <div className={styles.item}>
                        <button className={cn("button loading", styles.button)}>
                          <Loader className={styles.loader} color="white" />
                        </button>
                      </div>
                    )}
                    {success && (
                      <div className={cn(styles.item, styles.done)}>
                        <button className={cn("button success", styles.button)}>
                          Deposit Successful!
                        </button>
                      </div>
                    )}
                    {error && (
                      <div className={cn(styles.item, styles.error)}>
                        <button className={cn("button error", styles.button)}>
                          Deposit Failed
                        </button>
                      </div>
                    )}
                    {!loading && !error && !success && (
                      <Form
                        className={styles.form}
                        onSubmit={(e) => onDepositSubmit(e)}
                        placeholder="Enter an amount in USD"
                        setValue={setDeposit}
                        type="number"
                        name="price"
                        step={1}
                        min={0}
                      />
                    )}
                    {verified && (<div>You must verify your account before you can mint</div>)}
                  </div>
                </div>
                <div className={styles.item}>
                  <div className={styles.category}>Social</div>
                  <div className={styles.fieldset}>
                    <TextInput
                      className={styles.field}
                      label="portfolio or website"
                      name="Portfolio"
                      type="text"
                      placeholder={profile.url ? profile.url : "Enter URL"}
                      required
                      onChange={(e) => handleUrlChange(e.currentTarget.value)}
                    />
                    <TextInput
                      className={styles.field}
                      label="twitter"
                      name="Twitter"
                      type="text"
                      placeholder={
                        profile.twitter ? profile.twitter : "@twitter username"
                      }
                      required
                      onChange={(e) =>
                        handleTwitterChange(e.currentTarget.value)
                      }
                    />
                    <TextInput
                      className={styles.field}
                      label="instagram"
                      name="Instagram"
                      type="text"
                      placeholder={
                        profile.instagram
                          ? profile.instagram
                          : "@instagram username"
                      }
                      required
                      onChange={(e) =>
                        handleInstagramChange(e.currentTarget.value)
                      }
                    />
                  </div>
                </div>
              </div>
              <div className={styles.btns}>
                <button
                  className={cn("button", styles.button)}
                  onClick={(e) => handleSubmit(e)}
                >
                  {!updateSuccess ? 'Update Profile' : 'Update Successful!'}
                </button>
                {errors && (
                  <div>Your username has been taken already :(</div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileEdit;
